                            <div
                              key={option.value}
                              className={`px-4 py-2 text-sm hover:bg-gray-100 rounded-md cursor-pointer ${
                                filters.enumeration_type === option.value
                                  ? 'bg-blue-50 text-blue-700'
                                  : 'text-gray-700'
                              }`}
                              onClick={() => {
                                setFilters(prev => ({ ...prev, enumeration_type: option.value }));
                                setProviderTypeDropdownOpen(false);
                              }}
                            >
                              {option.label}
                            </div>
                          ))}
                        </div>
                      </div>
                    )}
                  </div>

                  {/* Address Type */}
                  <div className="relative">
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Address Type
                    </label>
                    <button 
                      className="w-full inline-flex items-center justify-between px-3 py-2 border border-gray-300 rounded-md text-sm text-gray-700 bg-white hover:bg-gray-50"
                      onClick={(e) => {
                        e.stopPropagation();
                        setAddressTypeDropdownOpen(!addressTypeDropdownOpen);
                      }}
                    >
                      <span>{filterConfig.address_purpose.options.find(opt => opt.value === filters.address_purpose)?.label}</span>
                      <ChevronDownIcon className={`w-4 h-4 transition-transform ${addressTypeDropdownOpen ? 'rotate-180' : ''}`} />
                    </button>
                    {addressTypeDropdownOpen && (
                      <div className="absolute z-10 mt-1 w-full bg-white border border-gray-200 rounded-md shadow-lg">
                        <div className="p-2">
                          {filterConfig.address_purpose.options.map((option) => (
                            <div
                              key={option.value}
                              className={`px-4 py-2 text-sm hover:bg-gray-100 rounded-md cursor-pointer ${
                                filters.address_purpose === option.value
                                  ? 'bg-blue-50 text-blue-700'
                                  : 'text-gray-700'
                              }`}
                              onClick={() => {
                                setFilters(prev => ({ ...prev, address_purpose: option.value }));
                                setAddressTypeDropdownOpen(false);
                              }}
                            >
                              {option.label}
                            </div>
                          ))}
                        </div>
                      </div>
                    )}
                  </div>
                </div>

                {/* Secondary Filters */}
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  {/* Provider Name */}
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Provider Name
                    </label>
                    <div className="grid grid-cols-2 gap-2">
                      <input
                        type="text"
                        placeholder="First Name"
                        className="rounded-md border border-gray-300 py-2 px-3 text-sm bg-white"
                        value={filters.first_name}
                        onChange={(e) => setFilters(prev => ({ ...prev, first_name: e.target.value }))}
                      />
                      <input
                        type="text"
                        placeholder="Last Name"
                        className="rounded-md border border-gray-300 py-2 px-3 text-sm bg-white"
                        value={filters.last_name}
                        onChange={(e) => setFilters(prev => ({ ...prev, last_name: e.target.value }))}
                      />
                    </div>
                  </div>

                  {/* Organization Name */}
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Organization Name
                    </label>
                    <input
                      type="text"
                      placeholder="Organization Name"
                      className="w-full rounded-md border border-gray-300 py-2 px-3 text-sm bg-white"
                      value={filters.organization_name}
                      onChange={(e) => setFilters(prev => ({ ...prev, organization_name: e.target.value }))}
                    />
                  </div>

                  {/* Location Details */}
                  <div className="grid grid-cols-2 gap-2">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        City
                      </label>
                      <input
                        type="text"
                        placeholder="Enter city"
                        className="w-full rounded-md border border-gray-300 py-2 px-3 text-sm bg-white"
                        value={filters.city}
                        onChange={(e) => setFilters(prev => ({ ...prev, city: e.target.value }))}
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        ZIP Code
                      </label>
                      <input
                        type="text"
                        placeholder="Enter ZIP"
                        className="w-full rounded-md border border-gray-300 py-2 px-3 text-sm bg-white"
                        value={filters.postal_code}
                        onChange={(e) => setFilters(prev => ({ ...prev, postal_code: e.target.value }))}
                      />
                    </div>
                  </div>
                </div>

                {/* Active Filter Tags */}
                <div className="mt-4 flex flex-wrap gap-2">
                  {activeSpecialties.map((specialty) => (
                    <span key={specialty} className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                      {specialty}
                    </span>
                  ))}
                  {activeLocations.map((location) => (
                    <span key={location} className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800">
                      {location}
                    </span>
                  ))}
                  {Object.entries(filters).map(([key, value]) => {
                    if (value && value !== "US" && key !== "enumeration_type") {
                      const label = filterConfig[key]?.options?.find(opt => opt.value === value)?.label || value;
                      return (
                        <span key={key} className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-purple-100 text-purple-800">
                          {label}
                        </span>
                      );
                    }
                    return null;
                  })}
                </div>
              </div>

              {/* Results Table */}
              <div className="overflow-x-auto">
                <table className="min-w-full divide-y divide-gray-200">
                  <thead className="bg-gray-50">
                    <tr>
                      <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Provider Name
                      </th>
                      <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Specialty
                      </th>
                      <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Address
                      </th>
                      <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Contact
                      </th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {currentData.map((provider) => (
                      <tr key={provider.id} className="hover:bg-gray-50">
                        <td className="px-6 py-4">
                          <div className="text-sm font-medium text-gray-900">{provider.name}</div>
                        </td>
                        <td className="px-6 py-4">
                          <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                            {provider.specialty}
                          </span>
                        </td>
                        <td className="px-6 py-4">
                          <div className="text-sm text-gray-500">{provider.hospital}</div>
                          <div className="text-sm text-gray-500">{provider.location}</div>
                        </td>
                        <td className="px-6 py-4">
                          <div className="text-sm text-blue-600">{provider.email}</div>
                          <div className="text-sm text-gray-500">{provider.phone}</div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>

              {/* Pagination */}
              <div className="bg-white px-4 py-3 flex items-center justify-between border-t border-gray-200 sm:px-6">
                <div className="flex-1 flex justify-between sm:hidden">
                  <button className="relative inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50">
                    Previous
                  </button>
                  <button className="ml-3 relative inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50">
                    Next
                  </button>
                </div>
                <div className="hidden sm:flex-1 sm:flex sm:items-center sm:justify-between">
                  <div>
                    <p className="text-sm text-gray-700">
                      Showing <span className="font-medium">10</span> of <span className="font-medium">9,000+</span> providers
                    </p>
                  </div>
                  <div>
                    <p className="text-sm text-gray-700">
                      Page {currentPage} of 900
                    </p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Features Section */}
      <div className="bg-gray-50 py-16">
        <div className="max-w-7xl mx-auto px-6">
          <div className="text-center mb-12">
            <h2 className="text-3xl font-bold text-gray-900">Faster Sales Cycles</h2>
            <p className="mt-4 text-lg text-gray-600">Everything you need to accelerate healthcare sales</p>
          </div>

          <div className="grid md:grid-cols-3 gap-8">
            <div className="bg-white p-6 rounded-xl border border-gray-200">
              <div className="w-12 h-12 bg-blue-100 rounded-lg flex items-center justify-center mb-4">
                <CheckIcon className="w-6 h-6 text-blue-600" />
              </div>
              <h3 className="text-lg font-semibold text-gray-900 mb-2">Verified Contact Information</h3>
              <p className="text-gray-600">Access accurate email addresses, phone numbers, and practice locations for healthcare providers.</p>
            </div>

            <div className="bg-white p-6 rounded-xl border border-gray-200">
              <div className="w-12 h-12 bg-blue-100 rounded-lg flex items-center justify-center mb-4">
                <CheckIcon className="w-6 h-6 text-blue-600" />
              </div>
              <h3 className="text-lg font-semibold text-gray-900 mb-2">Practice Details</h3>
              <p className="text-gray-600">Get comprehensive information about provider practices, specialties, and service areas.</p>
            </div>

            <div className="bg-white p-6 rounded-xl border border-gray-200">
              <div className="w-12 h-12 bg-blue-100 rounded-lg flex items-center justify-center mb-4">
                <CheckIcon className="w-6 h-6 text-blue-600" />
              </div>
              <h3 className="text-lg font-semibold text-gray-900 mb-2">Comprehensive Database</h3>
              <p className="text-gray-600">Access our extensive database of healthcare providers with verified information and practice details.</p>
            </div>
          </div>
        </div>
      </div>

      {/* How It Works */}
      <div className="py-16">
        <div className="max-w-7xl mx-auto px-6">
          <div className="text-center mb-12">
            <h2 className="text-3xl font-bold text-gray-900">How It Works</h2>
            <p className="mt-4 text-lg text-gray-600">Simple, powerful, and effective</p>
          </div>

          <div className="grid md:grid-cols-3 gap-8">
            <div className="text-center">
              <div className="w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center mx-auto mb-4">
                <span className="text-xl font-bold text-blue-600">1</span>
              </div>
              <h3 className="text-lg font-semibold text-gray-900 mb-2">Sign Up</h3>
              <p className="text-gray-600">Create your account and choose your subscription plan.</p>
            </div>

            <div className="text-center">
              <div className="w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center mx-auto mb-4">
                <span className="text-xl font-bold text-blue-600">2</span>
              </div>
              <h3 className="text-lg font-semibold text-gray-900 mb-2">Search & Filter</h3>
              <p className="text-gray-600">Find healthcare providers using our advanced search and filtering tools.</p>
            </div>

            <div className="text-center">
              <div className="w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center mx-auto mb-4">
                <span className="text-xl font-bold text-blue-600">3</span>
              </div>
              <h3 className="text-lg font-semibold text-gray-900 mb-2">Export & Connect</h3>
              <p className="text-gray-600">Export provider lists and connect with decision-makers through our platform.</p>
            </div>
          </div>
        </div>
      </div>

      {/* CTA Section */}
      <div className="bg-gray-50 py-16">
        <div className="max-w-4xl mx-auto px-6 text-center">
          <h2 className="text-3xl font-bold text-gray-900 mb-4">Start your 30-day free trial</h2>
          <p className="text-lg text-gray-600 mb-8">Access our complete healthcare provider database</p>
          <Link to="/signup" className="inline-block bg-blue-600 text-white px-8 py-3 rounded-lg font-medium hover:bg-blue-700">
            Get Started
          </Link>
          <p className="mt-4 text-sm text-gray-500">Your card will not be charged until your 30-day trial has ended.</p>
        </div>
      </div>

      {/* Trusted By Section */}
      <div className="border-t border-gray-200">
        <div className="max-w-7xl mx-auto px-6 py-16">
          <p className="text-center text-sm text-gray-600 mb-8">
            Trusted by leading healthcare sales teams at:
          </p>
          <div className="grid grid-cols-2 gap-8 md:grid-cols-4 items-center justify-items-center">
            {['IBM Healthcare', 'McKesson', 'Cardinal Health', 'Medtronic'].map((company) => (
              <div key={company} className="text-lg font-semibold text-gray-900">{company}</div>
            ))}
          </div>
        </div>
      </div>

      {/* Footer */}
      <footer className="border-t border-gray-200">
        <div className="max-w-7xl mx-auto px-6 py-8">
          <p className="text-center text-sm text-gray-500">
            © 2024 Provider Intelligence. All rights reserved.
          </p>
        </div>
      </footer>
    </div>
  );
};

export default LandingPage;
